{"changed":true,"filter":false,"title":"claim.rb","tooltip":"/app/models/claim.rb","value":"class Claim < ActiveRecord::Base\n\tbelongs_to :relation\n\tbelongs_to :company\n\n\tattr_accessible :claim_nr, :relation_id, :company_id\n\n\tvalidates       :claim_nr, presence: true\n\n  #------------------------------- CLASS METHODS -------------------------------\n=begin\n  # Get filtered insurances\n  def self.filter(params)\n    conditions = { }\n\n    # Filter on insurance number\n    unless params[:insurance_nr].blank?\n      conditions[:insurance_nr] = params[:insurance_nr]\n    end\n\n    # Filter on relation\n    unless params[:relation_id].blank?\n      conditions[:relation_id] = params[:relation_id]\n    end\n\n    # Filter on branch\n    unless params[:branch].blank?\n      conditions[:branch] = params[:branch]\n    end\n\n    # Filter on type\n    unless params[:insurance_type].blank?\n      conditions[:insurance_type] = params[:insurance_type]\n    end\n\n    # Filter on status\n    unless params[:status].blank?\n      conditions[:status] = params[:status]\n    end\n\n    # Filter on market\n    unless params[:market].blank?\n      conditions[:market] = params[:market]\n    end\n\n    # Filter on expiration date\n    unless params[:expiration_date].blank?\n      conditions[:main_premium_expiration_date] = params[:expiration_date]\n    end\n\n    # Find filtered relations\n    order(\"insurance_nr ASC\").where(conditions)\n  end\n=end\n  #------------------------------- INSTANCE METHODS -------------------------------\n\nend","undoManager":{"mark":55,"position":54,"stack":[[{"group":"doc","deltas":[{"start":{"row":0,"column":17},"end":{"row":0,"column":18},"action":"remove","lines":["e"]}]}],[{"group":"doc","deltas":[{"start":{"row":0,"column":16},"end":{"row":0,"column":17},"action":"remove","lines":["c"]}]}],[{"group":"doc","deltas":[{"start":{"row":0,"column":15},"end":{"row":0,"column":16},"action":"remove","lines":["n"]}]}],[{"group":"doc","deltas":[{"sta